"use client"

import { useState, useEffect } from "react"
import { Link } from "react-router-dom"
import { useAuth } from "../contexts/AuthContext"
import { fetchUniversityData } from "../services/universityService"

function Bookmarks() {
  const { currentUser } = useAuth()
  const [bookmarkedUniversities, setBookmarkedUniversities] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState("")

  useEffect(() => {
    async function loadBookmarkedUniversities() {
      try {
        const data = await fetchUniversityData()
        if (data && currentUser?.bookmarks) {
          const bookmarked = currentUser.bookmarks
            .map(id => data[id])
            .filter(university => university !== undefined)
          setBookmarkedUniversities(bookmarked)
        }
      } catch (err) {
        setError("Failed to load bookmarked universities")
      } finally {
        setLoading(false)
      }
    }

    loadBookmarkedUniversities()
  }, [currentUser])

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-xl font-semibold">Loading... ⏳</div>
      </div>
    )
  }

  if (error) {
    return (
      <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded">
        {error} 😕
      </div>
    )
  }

  if (!currentUser) {
    return (
      <div className="text-center py-8">
        <p className="text-xl mb-4">Please log in to view your bookmarks</p>
        <Link
          to="/login"
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
        >
          Login
        </Link>
      </div>
    )
  }

  if (bookmarkedUniversities.length === 0) {
    return (
      <div className="text-center py-8">
        <p className="text-xl">You haven't bookmarked any universities yet</p>
        <Link
          to="/questionnaire"
          className="mt-4 inline-block bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded"
        >
          Find Universities 🔍
        </Link>
      </div>
    )
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-8">Your Bookmarked Universities</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {bookmarkedUniversities.map((university) => (
          <div key={university.id} className="bg-white rounded-lg shadow-md overflow-hidden">
            <div className="h-48 bg-gray-200 flex items-center justify-center">
              <span className="text-gray-500 text-lg">🏛️ University Image</span>
            </div>
            <div className="p-6">
              <h2 className="text-xl font-bold mb-2">🏛️ {university.Name}</h2>
              <p className="text-gray-600 mb-4">📍 {university.Location}</p>
              <div className="space-y-2 mb-4">
                <p>
                  <span className="font-medium">🏆 Ranking:</span> {university.Ranking}
                </p>
                <p>
                  <span className="font-medium">💰 UG Fee:</span> {university.UG_Tuition_Fee}
                </p>
              </div>
              <Link
                to={`/university/${university.id}`}
                className="block w-full text-center bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
              >
                View Details
              </Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  )
}

export default Bookmarks
