"use client"

import { useState } from "react"
import { useQuestions } from "../contexts/QuestionsContext"
import { useAuth } from "../contexts/AuthContext"

function Questions() {
  const { questions, addQuestion, addAnswer } = useQuestions()
  const { currentUser } = useAuth()
  const [newQuestion, setNewQuestion] = useState({ title: "", content: "" })
  const [newAnswer, setNewAnswer] = useState({})

  const handleQuestionSubmit = (e) => {
    e.preventDefault()
    if (!currentUser) return

    if (!newQuestion.title.trim() || !newQuestion.content.trim()) return

    addQuestion({
      title: newQuestion.title,
      content: newQuestion.content,
      author: currentUser.name,
      authorEmail: currentUser.email
    })

    setNewQuestion({ title: "", content: "" })
  }

  const handleAnswerSubmit = (questionId) => {
    if (!newAnswer[questionId]?.trim()) return

    const answer = {
      content: newAnswer[questionId],
      author: currentUser.name,
      authorEmail: currentUser.email
    }

    addAnswer(questionId, answer)
    setNewAnswer(prev => ({ ...prev, [questionId]: "" }))
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-8">University Questions & Answers</h1>

      {currentUser && (
        <div className="bg-white rounded-lg shadow-md p-6 mb-8">
          <h2 className="text-xl font-semibold mb-4">Ask a Question</h2>
          <form onSubmit={handleQuestionSubmit}>
            <div className="mb-4">
              <input
                type="text"
                placeholder="Question title"
                value={newQuestion.title}
                onChange={(e) => setNewQuestion(prev => ({ ...prev, title: e.target.value }))}
                className="w-full p-2 border rounded-lg mb-2"
                required
              />
              <textarea
                placeholder="Your question"
                value={newQuestion.content}
                onChange={(e) => setNewQuestion(prev => ({ ...prev, content: e.target.value }))}
                className="w-full p-2 border rounded-lg"
                rows="3"
                required
              />
            </div>
            <button
              type="submit"
              className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600"
            >
              Ask Question
            </button>
          </form>
        </div>
      )}

      <div className="space-y-8">
        {questions.map(question => (
          <div key={question.id} className="bg-white rounded-lg shadow-md p-6">
            <div className="mb-4">
              <h2 className="text-xl font-semibold mb-2">{question.title}</h2>
              <p className="text-gray-600 mb-2">{question.content}</p>
              <div className="text-sm text-gray-500">
                Asked by {question.author} • {new Date(question.createdAt).toLocaleDateString()}
              </div>
            </div>

            <div className="border-t pt-4">
              <h3 className="text-lg font-semibold mb-4">Answers</h3>
              {question.answers.length > 0 ? (
                <div className="space-y-4">
                  {question.answers.map(answer => (
                    <div key={answer.id} className="bg-gray-50 p-4 rounded-lg">
                      <p className="text-gray-800">{answer.content}</p>
                      <div className="text-sm text-gray-500 mt-2">
                        Answered by {answer.author} • {new Date(answer.createdAt).toLocaleDateString()}
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="text-gray-500">No answers yet</p>
              )}

              {currentUser && (
                <div className="mt-4">
                  <textarea
                    value={newAnswer[question.id] || ""}
                    onChange={(e) => setNewAnswer(prev => ({ ...prev, [question.id]: e.target.value }))}
                    placeholder="Write your answer..."
                    className="w-full p-2 border rounded-lg mb-2"
                    rows="3"
                  />
                  <button
                    onClick={() => handleAnswerSubmit(question.id)}
                    className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600"
                  >
                    Post Answer
                  </button>
                </div>
              )}
            </div>
          </div>
        ))}

        {questions.length === 0 && (
          <p className="text-center text-gray-500">No questions have been asked yet.</p>
        )}
      </div>
    </div>
  )
}

export default Questions
