"use client"

import { useState, useEffect } from "react"
import { useParams, Link, useNavigate } from "react-router-dom"
import { fetchUniversityData } from "../services/universityService"
import { useAuth } from "../contexts/AuthContext"
import { Bookmark, BookmarkCheck } from "lucide-react"

function UniversityDetails() {
  const { id } = useParams()
  const navigate = useNavigate()
  const { currentUser, updateUser } = useAuth()
  const [university, setUniversity] = useState(null)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState("")
  const [isBookmarked, setIsBookmarked] = useState(false)

  useEffect(() => {
    async function loadData() {
      try {
        const data = await fetchUniversityData()
        if (data && data[id]) {
          setUniversity(data[id])
          // Check if university is bookmarked
          setIsBookmarked(currentUser?.bookmarks?.includes(Number(id)) || false)
        } else {
          setError("University not found")
        }
        setLoading(false)
      } catch (err) {
        setError("Failed to load university data")
        setLoading(false)
      }
    }

    loadData()
  }, [id, currentUser])

  const toggleBookmark = async () => {
    if (!currentUser) {
      navigate("/login")
      return
    }

    const bookmarks = currentUser.bookmarks || []
    const universityId = Number(id)
    let newBookmarks

    if (isBookmarked) {
      // Remove bookmark
      newBookmarks = bookmarks.filter(id => id !== universityId)
    } else {
      // Add bookmark
      newBookmarks = [...bookmarks, universityId]
    }

    // Update user's bookmarks
    await updateUser({ ...currentUser, bookmarks: newBookmarks })
    setIsBookmarked(!isBookmarked)
  }

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-xl font-semibold">Loading... ‚è≥</div>
      </div>
    )
  }

  if (error || !university) {
    return (
      <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded">
        {error || "University not found"} üòï
      </div>
    )
  }

  // Parse clubs and societies
  const clubs = university.Clubs_Societies ? university.Clubs_Societies.split(",").map((club) => club.trim()) : []

  // Parse scholarships
  const scholarships = university.Scholarship_Availability
    ? university.Scholarship_Availability.split(",").map((scholarship) => scholarship.trim())
    : []

  return (
    <div className="max-w-4xl mx-auto">
      <div className="mb-6 flex justify-between items-center">
        <Link to="/" className="text-blue-500 hover:text-blue-700">
          ‚Üê Back to Dashboard
        </Link>
        <button
          onClick={toggleBookmark}
          className={`flex items-center gap-2 px-4 py-2 rounded-lg ${
            isBookmarked
              ? "bg-blue-500 text-white hover:bg-blue-600"
              : "bg-gray-200 text-gray-700 hover:bg-gray-300"
          }`}
        >
          {isBookmarked ? (
            <>
              <BookmarkCheck className="w-5 h-5" />
              Bookmarked
            </>
          ) : (
            <>
              <Bookmark className="w-5 h-5" />
              Bookmark
            </>
          )}
        </button>
      </div>

      <div className="bg-white rounded-lg shadow-md overflow-hidden">
        <div className="h-48 bg-gray-200 flex items-center justify-center">
          <span className="text-gray-500 text-lg">üèõÔ∏è University Image</span>
        </div>

        <div className="p-6">
          <h1 className="text-3xl font-bold mb-2">üèõÔ∏è {university.Name}</h1>
          <p className="text-xl text-gray-600 mb-6">üìç {university.Location}</p>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-8 mb-8">
            <div>
              <h2 className="text-xl font-semibold mb-4">üìä Key Information</h2>
              <div className="space-y-3">
                <div>
                  <span className="font-medium">üèÜ Ranking:</span> {university.Ranking}
                </div>
                <div>
                  <span className="font-medium">üë®‚Äçüè´ Student to Faculty Ratio:</span>{" "}
                  {university.Student_to_Faculty_Ratio}
                </div>
                <div>
                  <span className="font-medium">üíº Employment Rate:</span> {university.Employment_Rate_After_Graduation}
                  %
                </div>
                <div>
                  <span className="font-medium">üåé International Students:</span>{" "}
                  {university.International_Student_Population}
                </div>
                <div>
                  <span className="font-medium">üè† On-Campus Accommodation:</span> {university.On_Campus_Accommodation}
                </div>
                <div>
                  <span className="font-medium">üî¨ Research Opportunities:</span> {university.Research_Opportunities}
                </div>
                <div>
                  <span className="font-medium">‚úàÔ∏è Exchange Program:</span> {university.Exchange_Students_Acceptance}
                </div>
              </div>
            </div>

            <div>
              <h2 className="text-xl font-semibold mb-4">üí∞ Tuition Fees</h2>
              <div className="space-y-3">
                <div>
                  <span className="font-medium">üéì Undergraduate:</span> {university.UG_Tuition_Fee}
                </div>
                <div>
                  <span className="font-medium">üéìüéì Masters:</span> {university.Masters_Tuition_Fee}
                </div>
              </div>

              {scholarships.length > 0 && (
                <div className="mt-6">
                  <h3 className="text-lg font-semibold mb-2">üèÜ Scholarships</h3>
                  <ul className="list-disc pl-5 space-y-1">
                    {scholarships.map((scholarship, index) => (
                      <li key={index}>{scholarship}</li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          </div>

          {clubs.length > 0 && (
            <div className="mb-8">
              <h2 className="text-xl font-semibold mb-4">üé≠ Clubs & Societies</h2>
              <div className="flex flex-wrap gap-2">
                {clubs.map((club, index) => (
                  <span key={index} className="bg-blue-100 text-blue-800 px-3 py-1 rounded-full text-sm">
                    {club}
                  </span>
                ))}
              </div>
            </div>
          )}

          <div className="mt-8 flex justify-center">
            <Link
              to="/questionnaire"
              className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded mr-4"
            >
              Find More Universities üîç
            </Link>
            <button
              className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
              onClick={() =>
                window.open(`https://www.google.com/search?q=${encodeURIComponent(university.Name)}`, "_blank")
              }
            >
              Visit Website üåê
            </button>
          </div>
        </div>
      </div>
    </div>
  )
}

export default UniversityDetails
