"use client"

// Add emojis to the university details page
import { useState, useEffect } from "react"
import { useParams, Link, useNavigate } from "react-router-dom"
import { useAuth } from "../contexts/AuthContext"
import { useQuestions } from "../contexts/QuestionsContext"
import { Bookmark, BookmarkCheck } from "lucide-react"

function UniversityDetails() {
  const { id } = useParams()
  const navigate = useNavigate()
  const { currentUser, updateUser } = useAuth()
  const { addQuestion, getQuestionsByUniversity } = useQuestions()
  const [university, setUniversity] = useState(null)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)
  const [isBookmarked, setIsBookmarked] = useState(false)
  const [newQuestion, setNewQuestion] = useState({ title: "", content: "" })
  const [universityQuestions, setUniversityQuestions] = useState([])

  useEffect(() => {
    const fetchUniversity = async () => {
      try {
        const response = await fetch(`http://universities.hipolabs.com/search?name=${id}`)
        const data = await response.json()
        if (data && data.length > 0) {
          const universityData = data[0]
          setUniversity(universityData)
          setIsBookmarked(currentUser?.bookmarks?.includes(universityData.name))
          setUniversityQuestions(getQuestionsByUniversity(universityData.name))
        } else {
          setError("University not found")
        }
      } catch (err) {
        setError("Failed to fetch university details")
      } finally {
        setLoading(false)
      }
    }

    fetchUniversity()
  }, [id, currentUser, getQuestionsByUniversity])

  const handleBookmark = () => {
    if (!currentUser) {
      navigate("/login")
      return
    }

    const updatedBookmarks = isBookmarked
      ? currentUser.bookmarks.filter(name => name !== university.name)
      : [...currentUser.bookmarks, university.name]

    updateUser({
      ...currentUser,
      bookmarks: updatedBookmarks
    })

    setIsBookmarked(!isBookmarked)
  }

  const handleQuestionSubmit = (e) => {
    e.preventDefault()
    if (!currentUser) {
      navigate("/login")
      return
    }

    if (!newQuestion.title.trim() || !newQuestion.content.trim()) return

    addQuestion({
      title: newQuestion.title,
      content: newQuestion.content,
      universityId: university.name,
      author: currentUser.name,
      authorEmail: currentUser.email
    })

    setNewQuestion({ title: "", content: "" })
    setUniversityQuestions(getQuestionsByUniversity(university.name))
  }

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="text-xl">Loading...</div>
      </div>
    )
  }

  if (error) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="text-xl text-red-500">{error}</div>
      </div>
    )
  }

  if (!university) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="text-xl">University not found</div>
      </div>
    )
  }

  // Parse clubs and societies
  const clubs = university.Clubs_Societies ? university.Clubs_Societies.split(",").map((club) => club.trim()) : []

  // Parse scholarships
  const scholarships = university.Scholarship_Availability
    ? university.Scholarship_Availability.split(",").map((scholarship) => scholarship.trim())
    : []

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="bg-white rounded-lg shadow-md p-6 mb-8">
        <div className="flex justify-between items-start mb-6">
          <h1 className="text-3xl font-bold">{university.name}</h1>
          <button
            onClick={handleBookmark}
            className={`px-4 py-2 rounded-lg ${
              isBookmarked
                ? "bg-red-500 hover:bg-red-600"
                : "bg-blue-500 hover:bg-blue-600"
            } text-white`}
          >
            {isBookmarked ? "Remove Bookmark" : "Bookmark"}
          </button>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <h2 className="text-xl font-semibold mb-2">Details</h2>
            <p className="text-gray-600">
              <span className="font-semibold">Country:</span> {university.country}
            </p>
            {university["state-province"] && (
              <p className="text-gray-600">
                <span className="font-semibold">State/Province:</span> {university["state-province"]}
              </p>
            )}
            {university.web_pages && university.web_pages.length > 0 && (
              <p className="text-gray-600">
                <span className="font-semibold">Website:</span>{" "}
                <a
                  href={university.web_pages[0]}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-500 hover:underline"
                >
                  {university.web_pages[0]}
                </a>
              </p>
            )}
          </div>

          <div>
            <h2 className="text-xl font-semibold mb-2">Domains</h2>
            {university.domains && university.domains.length > 0 ? (
              <ul className="list-disc list-inside text-gray-600">
                {university.domains.map((domain, index) => (
                  <li key={index}>{domain}</li>
                ))}
              </ul>
            ) : (
              <p className="text-gray-500">No domains available</p>
            )}
          </div>
        </div>
      </div>

      <div className="bg-white rounded-lg shadow-md p-6">
        <h2 className="text-2xl font-bold mb-6">Questions & Answers</h2>

        {currentUser && (
          <form onSubmit={handleQuestionSubmit} className="mb-8">
            <div className="mb-4">
              <input
                type="text"
                placeholder="Question title"
                value={newQuestion.title}
                onChange={(e) => setNewQuestion(prev => ({ ...prev, title: e.target.value }))}
                className="w-full p-2 border rounded-lg mb-2"
                required
              />
              <textarea
                placeholder="Your question"
                value={newQuestion.content}
                onChange={(e) => setNewQuestion(prev => ({ ...prev, content: e.target.value }))}
                className="w-full p-2 border rounded-lg"
                rows="3"
                required
              />
            </div>
            <button
              type="submit"
              className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600"
            >
              Ask Question
            </button>
          </form>
        )}

        <div className="space-y-6">
          {universityQuestions.map(question => (
            <div key={question.id} className="border-b pb-6">
              <h3 className="text-xl font-semibold mb-2">{question.title}</h3>
              <p className="text-gray-600 mb-2">{question.content}</p>
              <div className="text-sm text-gray-500 mb-4">
                Asked by {question.author} • {new Date(question.createdAt).toLocaleDateString()}
              </div>

              <div className="ml-4 space-y-4">
                {question.answers.map(answer => (
                  <div key={answer.id} className="bg-gray-50 p-4 rounded-lg">
                    <p className="text-gray-800">{answer.content}</p>
                    <div className="text-sm text-gray-500 mt-2">
                      Answered by {answer.author} • {new Date(answer.createdAt).toLocaleDateString()}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ))}

          {universityQuestions.length === 0 && (
            <p className="text-center text-gray-500">No questions have been asked yet.</p>
          )}
        </div>
      </div>
    </div>
  )
}

export default UniversityDetails
