"use client"

import { useState, useEffect } from "react"
import { Link } from "react-router-dom"
import { fetchUniversityData } from "../services/universityService"
import { useAuth } from "../contexts/AuthContext"

function Bookmarks() {
  const { currentUser } = useAuth()
  const [bookmarkedUniversities, setBookmarkedUniversities] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState("")

  useEffect(() => {
    async function loadData() {
      try {
        const allUniversities = await fetchUniversityData()
        // Get bookmarked university IDs from user's profile
        const bookmarkedIds = currentUser?.bookmarks || []
        const bookmarked = allUniversities.filter((_, index) => bookmarkedIds.includes(index))
        setBookmarkedUniversities(bookmarked)
        setLoading(false)
      } catch (err) {
        setError("Failed to load bookmarked universities")
        setLoading(false)
      }
    }

    loadData()
  }, [currentUser])

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-xl font-semibold">Loading... ‚è≥</div>
      </div>
    )
  }

  if (error) {
    return <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded">{error}</div>
  }

  if (bookmarkedUniversities.length === 0) {
    return (
      <div className="text-center py-12">
        <h1 className="text-3xl font-bold mb-4">Your Bookmarks</h1>
        <p className="text-lg text-gray-600 mb-6">You haven't bookmarked any universities yet.</p>
        <Link
          to="/"
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
        >
          Browse Universities
        </Link>
      </div>
    )
  }

  return (
    <div className="max-w-6xl mx-auto">
      <h1 className="text-3xl font-bold mb-8">Your Bookmarked Universities</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {bookmarkedUniversities.map((university, index) => (
          <div key={index} className="bg-white rounded-lg shadow-md overflow-hidden">
            <div className="h-40 bg-gray-200 flex items-center justify-center">
              <span className="text-gray-500 text-lg">üèõÔ∏è University Image</span>
            </div>
            <div className="p-4">
              <h3 className="text-xl font-semibold mb-2">{university.Name}</h3>
              <p className="text-gray-600 mb-2">üìç {university.Location}</p>
              <p className="text-gray-600 mb-2">üèÜ Ranking: {university.Ranking}</p>
              <Link
                to={`/university/${index}`}
                className="mt-2 inline-block bg-blue-500 hover:bg-blue-700 text-white py-2 px-4 rounded"
              >
                View Details
              </Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  )
}

export default Bookmarks
